import { Link } from 'react-router-dom';

import './movielist.scss';

const MovieList = ({ movies }) => {
  const urlRegex = /\s/g;
  // { movie.images['Poster Art'].url }
        // key={movie.id}

         /*.example3{
            .row{display: grid;}
            .one{ grid-template-rows: repeat(2, 100px); }
            .two{ grid-template-rows: 50px repeat(2, 100px) 150px; }
          } */
  return (
    <div className='container'>
      
      
        
       
      {movies.map(movie => (
          <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`}>


        
        

        
          <div className="columns is-multiline p-0 pt-6 last r-r">
            <div class="column is-full">
              <p class="has-text-white">Action Movies Collection</p>
            </div>
            <div class="column is-one-quarter">
              <img src="https://raw.githubusercontent.com/brixiobodino/coffeholic/main/image1.PNG" />
            </div>
          </div>

            
          </Link>
      ))}







    </div>
  );
}
 
export default MovieList;
