import { Link } from 'react-router-dom';

import './movielist.css';

const MovieList = ({ movies }) => {
  const urlRegex = /\s/g;
  return (
    <div className='container'>
      
      {movies.map(movie => (
        // { movie.images['Poster Art'].url }

          <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`}>

          <div className="accordion">
            <ul>
              <li>
                <div className="image_title" key={movie.id}>
                  <a href="#">Transformers: The Last Knight</a>
                </div>
                <img src={ movie.images['Poster Art'].url } alt="transformers4_640x320" border="0" />
              </li>
            </ul>
          </div>

            
          </Link>
      ))}







    </div>
  );
}
 
export default MovieList;
