import { useEffect } from 'react';

function Filter({
  movies,
  setFilteredCategories,
  activeCategory,
  setActiveCategory,
}) {
  useEffect(() => {
    if (activeCategory === 'movies') {
      setFilteredCategories(movies);
      return;
    }
    const filtered = movies.filter((movie) => movie.programType.includes(activeCategory));
    setFilteredCategories(filtered);
  }, [activeCategory]);

  // filtered releaseDate remove
  const releaseDates = [...movies].map((movie) => movie.releaseYear);
  // get movies
  const allMovies = [...movies].map((movie) => movie);

  const movieTypeMovie = movies.filter((type) => type.programType === 'movie');
  // console.log(movieTypeSeries);
  return (

    <div className="f-right select-filter-container">
      <span className="span-filter-title">Filter Movies</span>
      <select className="f-right select-filter" aria-label="Default select example">
        <option key="All" onClick={() => setActiveCategory(allMovies)}>All</option>
        <option key="Series" onClick={() => setActiveCategory('series')}>Series</option>
        <option key="Movies" onClick={() => setActiveCategory([movieTypeMovie])}>Movies</option>
        {releaseDates
          .filter((n, index) => releaseDates.indexOf(n) === index)
          .map((m) => (
            <option key={m} onClick={() => setActiveCategory(m)}>{m}</option>))}
      </select>
      <buttom onClick={() => setActiveCategory()}>Test</buttom>
    </div>
  );
}

export default Filter;
