import { useEffect } from 'react';
import { useState } from 'react';
function Filter({ movies }) {
    const [filteredType, setFilteredType] = useState([]);
    const [movieType, setMovieType] = useState('');
    useEffect(() => {
        if (movieType === movies) {
            setFiltered(movies);
            return;
        }
        const filtered = movies.filter((movie) => movie.programType.includes(movieType));
        setFilteredType(filtered);
    }, [movieType]);

    return (
        <div className="filter-c">
            <button type="button" onClick={() => setFilteredType(movies)}>All</button>
            <button type="button" onClick={() => setFilteredType('movie')}>Movies</button>
            <button type="button" onClick={() => setFilteredType('series')}>2016</button>
        </div>
    );
}

export default Filter;
