import { Link } from 'react-router-dom';
import { useState } from 'react';
import './movielist.css';
import Filter from './Filter';

function MovieList({ movies }) {
  const urlRegex = /\s/g;
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredResults, setFilteredResults] = useState([]);
  const [filteredCategories, setFilteredCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState(1998);

  const searchData = (value) => {
    setSearchTerm(value);
    if (searchTerm !== '') {
      const filteredData = movies.filter((movie) => Object.values(movie.title).join('').toLowerCase().includes(searchTerm.toLowerCase()));
      setFilteredResults(filteredData);
    } else {
      setFilteredResults(movies);
    }
  };

  return (

    <div>

      {/* NavBar for Search and Filter */}
      <nav className="navbar">
        <input className="search f-left" type="text" placeholder="Search..." onChange={(e) => searchData(e.target.value)} />
        <Filter movies={movies} setFilteredCategories={setFilteredCategories} activeCategory={activeCategory} setActiveCategory={setActiveCategory} />
      </nav>

      <div className="main">
        {
          searchTerm.length >= 3 ? (
            filteredResults.map((movie) => (

              <div key={movie.id} className="movie">
                <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`} style={{ textDecoration: 'none' }}>
                  <img src={movie.images['Poster Art'].url} alt="" />

                  <div className="movie-info">

                    <span>{movie.releaseYear}</span>
                    <span>{movie.programType}</span>
                  </div>
                  <div className="overview">

                    <h5>{ movie.title }</h5>

                  </div>
                </Link>
              </div>
            ))
          ) : (
            filteredCategories.map((movie) => (

              <div key={movie.id} className="movie">

                <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`} style={{ textDecoration: 'none' }}>
                  <img src={movie.images['Poster Art'].url} alt="" />

                  <div className="movie-info">
                    <span>{movie.releaseYear}</span>
                    <span>{movie.programType}</span>
                  </div>
                  <div className="overview">

                    <h5>{ movie.title }</h5>

                  </div>
                </Link>
              </div>

            ))
          )
        }
      </div>

    </div>
  );
}

export default MovieList;
