/* eslint-disable */
import { useEffect } from 'react';

function SortMovie({ movies, setFilteredYear, activeYear, setActiveYear }) {
    useEffect(() => {
        if (activeYear === null) {
            setFilteredYear(null);
            return;
        }
        const filtered = movies.filter((movie) => movie.releaseYear.includes(activeYear));
        setFilteredYear(filtered);
    }, [activeYear]);

    return (
        <div className="select-filter-container">
            <select name="releaseYear" className="select-filter" onChange={() => setActiveYear(activeYear) }>
                <option value="2022">{filtered.releaseYear}</option>
            </select>
        </div>
    );
}

export default SortMovie;
