/* eslint-disable */
import { useEffect } from "react";

function Filter({ movies, setFiltered, movieType, setMovieType }) {
    useEffect(() => {
        if (movieType === movies) {
            setFiltered(movies)
            return;
        }
    }, [movieType])
    return (
        <div className="filter-c">
            <button onClick={() => setFiltered('movies')}>All</button>
            <button onClick={() => setFiltered('movie')}>Movies</button>
            <button onClick={() => setFiltered('series')}>Series</button>
        </div>
    )
}

export default Filter;
