import { useState } from "react";
import useFetch from "../components/useFetch";

const Search = ( ) => {
    const { error, isPending, data: movies } = useFetch('http://localhost:1998/movies')
    const [search, setSearch] = useState('');

    const searchT = (e) => {
        setSearch(e.target.value);
    }
    console.warn(search);

    const data = movies.filter(movie => {
        return movie.title.toString().toLowerCase().includes()
    })

    return (
        <input className="search f-left" type="text" placeholder="Search..." value={search} onChange={searchT.bind(this)}/>
    )
};

export default Search;
