import { Dropdown } from 'bootstrap';
import { useEffect } from 'react';

function Filter({
  movies,
  setFilteredCategories,
  activeCategory,
  setActiveCategory,
}) {
  useEffect(() => {
    if (activeCategory === 'movies') {
      setFilteredCategories(movies);
      return;
    }
    const filtered = movies.filter((movie) => movie.programType.includes(activeCategory));
    setFilteredCategories(filtered);
  }, [activeCategory]);

  // filtered releaseDate remove
  const releaseDates = [...movies].map((movie) => movie.releaseYear);
  // get movies
  const allMovies = [...movies].map((movie) => movie);

  const movieTypeMovie = movies.filter((type) => type.programType === 'movie');
  // console.log(movieTypeSeries);
  return (

    <Dropdown>
      <Dropdown.Item onClick={() => setActiveCategory('movie')}>Action</Dropdown.Item>
    </Dropdown>

  );
}

export default Filter;
