import { Link } from 'react-router-dom';
import './movielist.css';

const MovieList = ({ movies }) => {
  const urlRegex = /\s/g;
  return (
    <div className="blog-list">
      {movies.map(movie => (
        <div className="blog-preview" key={movie.id} >
          <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`}>

          <div class="card" style="width: 18rem;">
            <img class="card-img-top" src={ movie.images['Poster Art'].url } alt="Card image cap" />
            <div class="card-body">
              <h2>{ movie.title }</h2>
              <p class="card-text"></p>
            </div>
          </div>

            
          </Link>
        </div>
      ))}
    </div>
  );
}
 
export default MovieList;
