import { Link } from 'react-router-dom';

const MovieList = ({ movies }) => {
  const urlRegex = /\s/g;
  return (
    <div className="blog-list">
      {movies.map(movie => (
        <div className="blog-preview" key={movie.id} >
          <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`}>
            <img src={ movie.images['Poster Art'].url } class="card-img-top" alt="..." />
            <div class="card-body">
		  	      <i class="fas fa-play play_button" data-toggle="tooltip" data-placement="bottom" title="Play Trailer">
		  	</i>
		    <h5 class="card-title">Toy Story 4</h5>
		   		<span class="movie_info">2019</span>
		   		<span class="movie_info float-right"><i class="fas fa-star"></i> 9 / 10</span>
		  </div>

            <h2>{ movie.title }</h2>
          </Link>
        </div>
      ))}
    </div>
  );
}
 
export default MovieList;
