/* eslint-disable */
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
// Get All Movies Data and Build a movie card for each one / add a unique link for each movie to redirect view movie details page
function MovieCard({ filteredType, moreMovies }) {
    const urlRegex = /\s/g;
    return (
      filteredType.slice(0, moreMovies).map((movie) => (

            <motion.div key={movie.id} className="movie" Layout>
              <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`} style={{ textDecoration: 'none' }}>
                <img src={movie.images['Poster Art'].url} alt="" />
                <div className="movie-info">
                  <span>{movie.releaseYear}</span>
                  <span>{movie.programType}</span>
                </div>
                <div className="overview">
                  <h5>{movie.title}</h5>
                </div>
              </Link>
            </motion.div>
          ))
    );
}
export default MovieCard;
