/* eslint-disable */
import { useEffect } from 'react';

function SortMovie({ movies, setSortType, activeYear, setActiveYear }) {
    // get release years
    const getYears = movies.map((movie) => movie.releaseYear);
    // remove duplicates
    const filterYears = getYears.filter(function (year, d) {
        return getYears.indexOf(year) == d;
    }).sort(function (a, b) { return a - b })
    // console.log(filterYears)

    useEffect(() => {
        if (activeYear === null) {
            setSortType(null);
            return;
        }
        const filtered = movies.filter((movie) => movie.releaseYear.includes(activeYear));
        setSortType(filtered);
    }, [activeYear]);

    return (
        <div className="select-filter-container" onChange={() => setActiveYear()}>
            <select className="select-filter">
                <option value="all">Sort By Year</option>
                {filterYears.map((year) => (
                    <option value={year}>{year}</option>
                ))}
            </select>
        </div>
    );
}

export default SortMovie;
