import logo from './logo.svg';
import './App.css';
import Records from './movies/movies.json';

function App() {
  const MovieList = (props) => {

            return (
                <div className="row">

                    {props.Records.map((movie) => (
                        <div className="col-lg-4" key={movie.id}>
                            <div className="card mb-4 shadow-sm">
                                <img src={movie.entries.images['Poster Art'].url} className="card-img-top" alt="Sample Movie" />
                                <div className="card-body">
                                    <h5 className="card-title">{movie.name}</h5>
                                    <p className="card-text">{movie.overview}</p>
                                    <div className="d-flex justify-content-between align-items-center">
                                        <button type="button" onClick={(event)=>props.deleteMovieProp(movie)} className="btn btn-md btn-outline-danger">Delete</button>
                                        <h2><span className="badge bg-secondary">{movie.rating}</span></h2>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}

                </div>
            )
    }
}

export default App;
