import { /* useHistory, */ useParams } from "react-router-dom";
import useFetch from "../components/useFetch";
import './moviedetails.scss';

const MovieDetails = () => {
  const { id } = useParams();

  const { data: movie, error, isPending } = useFetch('http://localhost:1998/movies/' + id);
  
  
  
  // const history = useHistory();

  // handleClick Function to delete a movie
    /*
  const handleClick = () => {
    fetch('http://localhost:1998/movies/' + movie.id, {
      method: 'DELETE'
    }).then(() => {
      history.push('/');
    }) 
  } */

  return (
    <div>
      { isPending && <div>Loading...</div> }
      { error && <div>{ error }</div> }
      { movie && (

        <div className="movie-card">
          
          <div className="container">
            
            <a href="#"><img src={ movie.images['Poster Art'].url } alt="cover" className="cover" /></a>
                
            <div className="hero">
                    
              <div className="details">
              
                <div className="title1">{ movie.title }<span>PG-13</span></div>

                <div className="title2">The Battle of the Five Armies</div>    
                
                <fieldset className="rating">
                    <input type="radio" id="star5" name="rating" value="5" /><label className = "full" for="star5" title="Awesome - 5 stars"></label>

          </fieldset>
                
                <span className="likes">109 likes</span>
                
              </div>
              
            </div>
            
            <div className="description">
              
              <div className="column1">
                <span className="tag">action</span>
                <span className="tag">fantasy</span>
                <span className="tag">adventure</span>
              </div>
              
              <div class="column2">
                
                <p>Bilbo Baggins is swept into a quest to reclaim the lost Dwarf Kingdom of Erebor from the fearsome dragon Smaug. Approached out of the blue by the wizard Gandalf the Grey, Bilbo finds himself joining a company of thirteen dwarves led by the legendary warrior, Thorin Oakenshield. Their journey will take them into the Wild; through... <a href="#">read more</a></p>
                
                
                
                
              </div>
            </div>
            {/* Delet Movie <button onClick={handleClick}>delete</button> */} 
          
          </div>
        </div>








      )}
    </div>
  );
}
 
export default MovieDetails;
