import { useEffect } from 'react';

function SortMovie({ movies, setActiveYear, activeCategory, setFilteredType }) {
    useEffect(() => {
        if (activeCategory === movies) {
            setFilteredType(movies);
            return;
        }
        const filtered = movies.filter((movie) => movie.programType.includes(activeCategory));
        setFilteredType(filtered);
    }, [activeCategory]);

    return (
        <div className="select-filter-container">
            <select name="releaseYear" className="select-filter" onChange={onSortChange}>
                <option value="2022">AMHF</option>
                <option value="2021">test</option>
            </select>
        </div>
    );
}

export default SortMovie;
