import { useEffect } from 'react';

function Filter(
    { movies,setFiltered, movieType, setMovieType }) {
    useEffect(() => {
        if (movieType === movies) {
            setFiltered(movies);
            return;
        }
        const filterdType = movies.filter((movie) => movie.programType.includes(movieType));
        setFiltered(filterdType);
    }, [movieType])

    return (
        <div className="filter-c">
            <button onClick={() => setFiltered(movies)}>All</button>
            <button onClick={() => setFiltered('movie')}>Movies</button>
            <button onClick={() => setFiltered('series')}>2016</button>
        </div>
    );
}

export default Filter;
