import { useHistory, useParams } from "react-router-dom";
import useFetch from "../components/useFetch";

const MovieDetails = () => {
  const { id } = useParams();

  const { data: movie, error, isPending } = useFetch('http://localhost:1998/movies/' + id);
  const history = useHistory();


  // handleClick Function to delete a movie
    /*
  const handleClick = () => {
    fetch('http://localhost:1998/movies/' + movie.id, {
      method: 'DELETE'
    }).then(() => {
      history.push('/');
    }) 
  } */

  return (
    <div className="blog-details">
      { isPending && <div>Loading...</div> }
      { error && <div>{ error }</div> }
      { movie && (

        <div>
          <img src={ movie.images['Poster Art'].url } class="card-img-top" alt="..." />
          <h2>{ movie.title }</h2>


          
          {/* Delet Movie <button onClick={handleClick}>delete</button> */} 
        </div>
      )}
    </div>
  );
}
 
export default MovieDetails;
