import './movielist.css';

const MovieList = ({ movies }) => {
  const urlRegex = /\s/g;
  return (
    <div className='accordion'>
      
      {movies.map(movie => (
        // { movie.images['Poster Art'].url }

          <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`}>


          <div className='movie' style={{backgroundImage: `url(${movie.images['Poster Art'].url})`}}>
                
                <h2 className='movie__title'>{movie.title}</h2>
                
                <span className='movie__description'>{movie.description}</span>
                
                {/* <div className='movie__infos'>
