import { Link } from 'react-router-dom';

import './movielist.less';

const MovieList = ({ movies }) => {
  const urlRegex = /\s/g;
  return (
    <div className='accordion'>
      
      {movies.map(movie => (
        // { movie.images['Poster Art'].url }
        // key={movie.id}
        /*.example3{
            .row{display: grid;}
            .one{ grid-template-rows: repeat(2, 100px); }
            .two{ grid-template-rows: 50px repeat(2, 100px) 150px; }
          } */

          <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`}>


<section class="container">
    <div class="row two">
        <div class="col- repeat">
            <article class="material-card Red">
                <h2>
                    <span>Christopher Walken</span>
                    <strong>
                        <i class="fa fa-fw fa-star"></i>
                        The Deer Hunter
                    </strong>
                </h2>
                <div class="mc-content">
                    <div class="img-container">
                        <img class="img-responsive" src="https://material-cards.s3-eu-west-1.amazonaws.com/thumb-christopher-walken.jpg" />
                    </div>
                    <div class="mc-description">
                        He has appeared in more than 100 films and television shows, including The Deer Hunter, Annie Hall, The Prophecy trilogy, The Dogs of War ...
                    </div>
                </div>
                <a class="mc-btn-action">
                    <i class="fa fa-bars"></i>
                </a>
                <div class="mc-footer">
                    <h4>
                        Social
                    </h4>
                    <a class="fa fa-fw fa-facebook"></a>
                    <a class="fa fa-fw fa-twitter"></a>
                    <a class="fa fa-fw fa-linkedin"></a>
                    <a class="fa fa-fw fa-google-plus"></a>
                </div>
            </article>
        </div>
    </div>
</section>

            
          </Link>
      ))}







    </div>
  );
}
 
export default MovieList;
