import { Link } from 'react-router-dom';
import { useState } from 'react';
import Slider from 'react-slick';
import './movielist.css';

function MovieList({ movies }) {
  const urlRegex = /\s/g;
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredResults, setFilteredResults] = useState([]);
  const [moreMovies, setMoreMovies] = useState(20);
  const loadMoreMovies = () => {
    setMoreMovies((lastValue) => lastValue * 2);
  };

  // Search Function - By Title - minimum 3 letters
  const searchData = (value) => {
    setSearchTerm(value);
    if (searchTerm !== '') {
      const filteredData = movies.filter((movie) => Object.values(movie.title).join('').toLowerCase().includes(searchTerm.toLowerCase()));
      setFilteredResults(filteredData);
    } else {
      setFilteredResults(movies);
    }
  };

  // settings for the slider
  const settings = {
    className: 'center',
    centerMode: true,
    infinite: true,
    centerPadding: '60px',
    slidesToShow: 3,
    speed: 500,
    rows: 2,
    slidesPerRow: 2,
  };

  // Get All Movies and Build a movie card for each one 
  const showMovies = movies.slice(0, moreMovies).map((movie) => (

    <div key={movie.id} className="movie">
      <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`} style={{ textDecoration: 'none' }}>
        <img src={movie.images['Poster Art'].url} alt="" />

        <div className="movie-info">
          <span>{movie.releaseYear}</span>
          <span>{movie.programType}</span>
        </div>
        <div className="overview">

          <h5>{movie.title}</h5>

        </div>
      </Link>
    </div>
  ))

  return (

    <div className="main-fix">

      {/* NavBar for Search and Filter */}
      <nav className="navbar">
        <input className="search f-left" type="text" placeholder="Search..." onChange={(e) => searchData(e.target.value)} />
      </nav>

      <div className="main">
        {
          searchTerm.length >= 3 ? (
            filteredResults.map((movie) => (

              <div key={movie.id} className="movie">
                <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`} style={{ textDecoration: 'none' }}>
                  <img src={movie.images['Poster Art'].url} alt="" />

                  <div className="movie-info">

                    <span>{movie.releaseYear}</span>
                    <span>{movie.programType}</span>
                  </div>
                  <div className="overview">

                    <h5>{movie.title}</h5>

                  </div>
                </Link>
              </div>
            ))
          ) : ( showMovies
          )
        }
      </div>
      <div className="load-more-container">
        {moreMovies <= 100 ? <button type="button" className="load-more" onClick={loadMoreMovies}>Load More</button> : null}
      </div>
    </div>
  );
}

export default MovieList;
