import { Link } from "react-router-dom";
import './nav.css'
import useFetch from "../components/useFetch";


const Navbar = ({ movies }) => {
  {movies.map(movie => (
      

    <div key={movie.id} className="movie" >
      {/* 
        <h2>{ movie.title }</h2>
      */}
      <Link to={`/movies/${movie.id}/${movie.title.toLowerCase().replace(urlRegex, '-')}`} style={{ textDecoration: 'none' }}>
        <img src={ movie.images['Poster Art'].url } alt="" />
      
          <div className="movie-info">

              <span>{RandomRating(3.2, 8.7)}/{maxNumber}</span>
              <span>{movie.releaseYear}</span>
              <span>{movie.programType}</span>
          </div>
          <div className="overview">
            
            <h5>{ movie.title }</h5>
            
          </div>  
          </Link>
    </div>
    

  ))}
  return (
    
/*
<Link to="/">Movies</Link>

<Link to="/">Home</Link>

<Link to="../pages/create">New Movie</Link>
*/

            <div>
            <nav className="navbar">
              <span className="navbar-brand f-right" ><Link to="/" className="nav-link">Movies App</Link></span>
              <span className="navbar-brand"><Link to="/" className="nav-link">Home</Link></span>
              <span className="navbar-brand"><Link to="../pages/create" className="nav-link">Add New Movie</Link></span>


              <input className="search f-left" type="text" placeholder="Search..." />
            </nav>
            </div>



  );
}
 
export default Navbar;
