import { useState } from "react";
import { useHistory } from "react-router-dom";



const Create = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [programType, setProgramType] = useState('series');
  const [releaseYear, setReleaseYear] = useState('');

  const years = (new Date()).getFullYear();
  years = Array.from(new Array(20),(val, index) => index + year);


  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    const movie = { title, description, programType, releaseYear };

    fetch('http://localhost:1998/movies/', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(movie)
    }).then(() => {
      history.push('/');
    })
  }

  return (
    <div className="create">
      <h2>Add a New Movie</h2>
      <form onSubmit={handleSubmit}>
        <label>Movie title:</label>
        <input 
          type="text" 
          required 
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label>Description</label>
        <textarea
          required
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        ></textarea>
        <label>Program Type</label>
        <select
          value={programType}
          onChange={(e) => setProgramType(e.target.value)}
        >
          <option value="series">movie</option>
          <option value="movie">series</option>
        </select>
        <select>
          {
            this.years.map((year, index) => {
              return <option key={`years${index}`} value={year}>{year}</option>
            })
          }
        </select>
        <button>Add Movie</button>
      </form>
    </div>
  );
}
 
export default Create;
